var tag = document.createElement('script');
tag.src = "https://www.youtube.com/player_api";
var firstScriptTag = document.getElementsByTagName('script')[0];
firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

var playerInfoList = playerVideos;
var players = [];

function createPlayer(playerInfo) {
    apf_video = new YT.Player(playerInfo.id, {
        playerVars: {
            'autoplay': 1,
            'controls': 0,
            'autohide':1,
            'wmode':'opaque',
            'rel':0,
            'showinfo':0,
            'loop':1,
            'iv_load_policy':3,
            'enablejsapi':0,
            'version':3,
            'allowfullscreen':0,
            'playlist':playerInfo.videoId,
            'html5':1
        },
        videoId: playerInfo.videoId,
        events: {
            'onReady': onPlayerReady
        }
    });

    return apf_video;

}

function onYouTubeIframeAPIReady() {
    if (typeof playerInfoList === 'undefined') { return;}

    for (var i = 0; i < playerInfoList.length; i++) {
        var curplayer = createPlayer(playerInfoList[i]);
        players[i] = curplayer;
    }
}

function onPlayerReady(event) {
    event.target.mute();
    event.target.setPlaybackQuality('hd720');
}

jQuery(document).ready(function($) {
    $('#home_banners').lightSlider({
        item: 1,
        loop: false,
        auto: true,
        pause: 6000,
        selector: '.banner',
        easing: 'cubic-bezier(0.25, 0, 0.25, 1)',
        controls: false,
        enableDrag: false,
        slideMargin: 0
    });


    // --------------- Controls --------------- //
    $('.video__play').click(function(e) {
        e.preventDefault();
        apf_video.unMute();
        apf_video.seekTo(0);
        $(".banner__caption").addClass("off");
        $(".banner__video__controls").addClass("on");
    });

    $('body').on("click", '.video__mute, .video__unmute', function(e) {
        e.preventDefault();
        if ($(this).hasClass('video__mute')) {
            apf_video.mute();
        } else {
            apf_video.unMute();
        }

        $(this).toggleClass('video__mute video__unmute');
        $(this).children().toggleClass('ion-volume-high ion-volume-mute');
    });
});


function gf_form_ajax(clicked_element) {
    var $ = jQuery;

    // set variables
    var gf_id = clicked_element.attr('data-gf-id');
    var event_id = clicked_element.attr('data-event-id');

    // show pop-up
    $('.gf__modal__form .wp__loading').addClass('is__loading');
    $('.gf__modal__form__overlay').addClass('open');
    $('body').addClass('no__scroll');

    // AJAX results
    $.ajax({
        url: fl1_ajax_object.ajax_url,
        dataType: 'html',
        type: 'GET',
        data: ({
            'action' : 'gf_ajax_form',
            'security' : fl1_ajax_object.ajax_nonce,
            'gf_id' : gf_id,
            'event_id' : event_id
        }),

        success: function(data) {
            $('.wp__loading').removeClass('is__loading');
            $('#gf_ajax').html(data);
            if(window['gformInitDatepicker']) {gformInitDatepicker();}
        },

        error: function(xhr, ajaxOptions, thrownError){
            alert(xhr.status);
        }
    });
}

jQuery(document).ready(function($){

    $(document).on('click', '[data-gf-ajax-trigger]', function(e) {
        e.preventDefault();

        clicked_element = $(this);
        gf_form_ajax(clicked_element);
    });

    $(document).on('click', '.gf__modal__form .close', function(e) {
        e.preventDefault();

        // hide pop-up
        $('.gf__modal__form__overlay').removeClass('open');
        $(this).closest('.flexible__content').removeAttr('style');
        $('body').removeClass('no__scroll');

        setTimeout(function() {
            // destroy form
            $('#gf_ajax').html('');
        }, 500);

    });

});


